update sbr.data_element_concepts dec set dec.CONTE_IDSEQ = (select CONTE_IDSEQ from SBR.CONTEXTS where name = '"&A3&"'), dec.change_note = dec.change_note || ' "&B7&"' where dec.CONTE_IDSEQ = (select CONTE_IDSEQ from SBR.CONTEXTS where name = '"&A2&"') and (upper(dec.created_by) = '"&A6&"' or lower(dec.created_by) = '"&A6&"') and trunc(dec.date_created) >= to_date('"&A4&"', 'YYYY-MM-DD') and trunc(dec.date_created) <= to_date('"&A5&"', 'YYYY-MM-DD');

update sbr.data_elements de set de.CONTE_IDSEQ = (select CONTE_IDSEQ from SBR.CONTEXTS where name = '"&A3&"'), de.change_note = de.change_note || ' "&B7&"' where  de.CONTE_IDSEQ = (select CONTE_IDSEQ from SBR.CONTEXTS where name = '"&A2&"') and (upper(de.created_by) = '"&A6&"' or lower(de.created_by) = '"&A6&"') and trunc(de.date_created) >= to_date('"&A4&"', 'YYYY-MM-DD') and trunc(de.date_created) <= to_date('"&A5&"', 'YYYY-MM-DD');

update sbr.value_domains vd set vd.CONTE_IDSEQ = (select CONTE_IDSEQ from SBR.CONTEXTS where name = '"&A3&"'), vd.change_note = vd.change_note || ' "&B7&"' where  vd.CONTE_IDSEQ = (select CONTE_IDSEQ from SBR.CONTEXTS where name = '"&A2&"') and (upper(vd.created_by) = '"&A6&"' or lower(vd.created_by) = '"&A6&"') and trunc(vd.date_created) >= to_date('"&A4&"', 'YYYY-MM-DD') and trunc(vd.date_created) <= to_date('"&A5&"', 'YYYY-MM-DD');

update SBR.ADMINISTERED_COMPONENTS ac set ac.CONTE_IDSEQ = (select CONTE_IDSEQ from SBR.CONTEXTS where name = '"&A3&"'), ac.change_note = ac.change_note || ' "&B7&"' where ac.public_id in ( select ac.public_id from SBR.ADMINISTERED_COMPONENTS ac, SBR.CONTEXTS c  where  ac.conte_idseq = c.conte_idseq and ac.CONTE_IDSEQ = (select CONTE_IDSEQ from SBR.CONTEXTS where name = '"&A2&"') and ac.actl_name in ('DATAELEMENT', 'VALUEDOMAIN', 'DE_CONCEPT') and (upper(ac.created_by) = '"&A6&"' or lower(ac.created_by) = '"&A6&"') and trunc(ac.date_created) >= to_date('"&A4&"', 'YYYY-MM-DD') and trunc(ac.date_created) <= to_date('"&A5&"', 'YYYY-MM-DD'));
